from rest_framework import serializersfrom collectors.api.serializers import CountrySerializerfrom . import modelsclass CollectionTypeSerializer(serializers.ModelSerializer):    """    Serializer for CollectionType    """    class Meta:        model = models.CollectionType        fields = (            'id',            'name',        )class CategoryTypeSerializer(serializers.ModelSerializer):    """    Serializer for CollectionType    """    class Meta:        model = models.Category        fields = (            'id',            'name',        )class CollectionSubTypeSerializer(serializers.ModelSerializer):    """    Serializer for CollectionType    """    type = CollectionTypeSerializer()    class Meta:        model = models.CollectionSubType        fields = (            'id',            'name',            'type',        )class CollectionListSerializer(serializers.ModelSerializer):    """    Serializer for Collection    """    category = CategoryTypeSerializer()    subtype = CollectionSubTypeSerializer()    country = CountrySerializer()    class Meta:        model = models.Collection        fields = (            'id',            'name',            'set',            'Count',            'views_no',            'user_possession',            'likes',            'publicated',            'country',            'reverse_photo',            'obverse_photo',            'subtype',            'description',            'category',        )class CollectionSerializer(serializers.ModelSerializer):    class Meta:        model = models.Collection        fields = (            'id',        )